function newTurtle=Forward(turtle,distance)distanceOk=turtle.nextMark-turtle.inkLength;if distanceOk>distance || turtle.ink==0	distanceOk=distance;endturtle.position=turtle.position+turtle.direction*distanceOk;if turtle.print	fprintf(' Forward %.0f;',distanceOk);endif turtle.ink	turtle.inkLength=turtle.inkLength+distanceOk;	if distance>distanceOk		turtle=Mark(turtle);		turtle=Forward(turtle,distance-distanceOk);	endendnewTurtle=turtle;