clear all;load snakes;index = 1:10;figure;cols=2;rows=4;letterchart = zeros(2,cols*size(signal1,1));for i=1:rows;	temp = shuffle(index);	eval(sprintf('row = signal%d./(2.^i);',temp(1)))	for j=2:cols;		clear signal;		eval(sprintf('signal = signal%d./(2.^i);',temp(j)))		row = [row,signal];	end	letterchart = [letterchart;row];endletterchart = 127*(letterchart+2/3);noise = randi(floor(127/3),ceil(size(letterchart)./2));noise = expand(noise,2);letterchart = letterchart + noise(1:size(letterchart,1),1:size(letterchart,2));clear noise% letterchart = letterchart./max(max(letterchart));% letterchart = letterchart - min(min(letterchart)) ;% letterchart = letterchart.*127;saveaseps('chart.eps',letterchart);% screen(0,'saveaseps','row.eps',letterchart);showim(letterchart);